mapper.update:

UPDATE DATA
SET DATA=#{data }
WHERE DSEQ=#{dseq }

mapper.delete:

DELETE FROM DATA
WHERE INUM=#{pdseq}
AND CHAR_LENGTH(data)<#{length}


DELETE FROM DATA
WHERE INUM=#{pdseq} 
AND CHAR_LENGTH(data)<=#{length}


DELETE FROM DATA
WHERE INUM=#{pdseq} 
AND CHAR_LENGTH(data)>#{length}


DELETE FROM DATA
WHERE INUM=#{pdseq}
AND CHAR_LENGTH(data)>=#{length}


DELETE FROM DATA
WHERE INUM=#{pdseq}
AND CHAR_LENGTH(data)=#{length}


DELETE FROM DATA
WHERE INUM=#{pdseq}
AND CHAR_LENGTH(data)!=#{length}
